/**
 * –ö–ª–∞—Å—Å –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è
*/

class Achievement {

    /**
     * @param {Object} object –û–±—ä–µ–∫—Ç —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è
     * @param {string} object.title –ù–∞–∑–≤–∞–Ω–∏–µ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è
     * @param {string} object.text –û–ø–∏—Å–∞–Ω–∏–µ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è
     * @param {string} object.picture –ö–∞—Ä—Ç–∏–Ω–∫–∞ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è
     * @param {string} object.story –ö–æ–¥ –∏—Å—Ç–æ—Ä–∏–∏ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è
     */

    constructor(object) {
        this.title = object.title;
        this.text = '<hr>' + object.text+ '<p>';
        this.picture = 'pictures/' + object.picture + '.png';
        this.unlocked = 0;
        this.story = object.story;
        this.Init();
    }

    /** –°–æ–∑–¥–∞—ë–º –∏ –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–π */

    Init() {
        this.a = document.createElement('achievement');
        this.b = document.createElement('img');
        this.b.src = this.picture
        this.c = document.createElement('ATitle');
        this.c.innerHTML = this.title;
        this.d = document.createElement('AText');
        this.d.innerHTML = this.text;
        this.e = document.createElement('img');
        this.e.src = 'pictures/Items/Lock.png';
        this.e.id= 'lock';
        AchievementsField.appendChild(this.a);
        this.a.appendChild(this.b);
        this.a.appendChild(this.e);
        this.a.appendChild(this.c);
        this.a.appendChild(this.d);  
    }

    /** –û—Ç–∫—Ä—ã–≤–∞–µ–º –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–µ - –≤—ã–≤–æ–¥–∏–º —Å–æ–æ–±—â–µ–Ω–∏–µ, –º–µ–Ω—è–µ–º –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –∏ —Å–æ—Ö—Ä–∞–Ω—è–µ–º –ø—Ä–æ–≥—Ä–µ—Å—Å */

    Unlock() {
        if(this.unlocked!=1){
            Game.Inventory_Message('üîì '+ this.title);
            Game.SendData('–ø–æ–ª—É—á–∞–µ—Ç –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–µ: '+ this.title);
        }
        this.unlocked = 1;
        this.a.setAttribute('class', 'activeachievement');
        this.b.style.display= 'grid';
        this.e.style.display = 'none';
        Game.Progress.AchievementsSave();
    }

    /** –í–∏–∑—É–∞–ª—å–Ω–æ –ø—Ä—è—á–µ–º —ç–ª–µ–º–µ–Ω—Ç –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è */

    Show(){
        this.a.setAttribute('class','hide');
        this.a.style.display = 'grid';
        setTimeout(()=>{this.a.setAttribute('class','show');},200);
    }

    /** –í–∏–∑—É–∞–ª—å–Ω–æ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —ç–ª–µ–º–µ–Ω—Ç –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è */

    Hide(){
        this.a.style.display = 'none';
    }
}
/** –ö–ª–∞—Å—Å —Å—Ü–µ–Ω—ã - —Ç–µ–∫—Å—Ç, –∫–∞—Ä—Ç–∏–Ω–∫–∞, —Ç–µ–∫—Å—Ç –∫–Ω–æ–ø–æ–∫, –¥–µ–π—Å—Ç–≤–∏—è –∫–Ω–æ–ø–æ–∫, –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å –∫–Ω–æ–ø–æ–∫, –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ–µ –¥–µ–π—Å—Ç–≤–∏–µ */
class Scene {
    /**
     *
     * @param {
     * {
     *  buttontext: string[]|undefined,
     *  condition: function|undefined,
     *  background: string|undefined,
     *  buttonaction: function[],
     *  text: string
     * }
     * } info —Å—é–¥–∞ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –≤—Å–µ –≤–≤–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
     *
     * @param  {string} info.text –¢–µ–∫—Å—Ç —Å–ª–∞–π–¥–∞
     *
     * @param  {string[]|undefined} info.buttontext –¢–µ–∫—Å—Ç –∫–Ω–æ–ø–æ–∫
     *
     * @param  {function[]} info.buttonaction –î–µ–π—Å—Ç–≤–∏—è –∫–Ω–æ–ø–æ–∫
     *
     * @param  {boolean[]} info.buttonactive –ê–∫—Ç–∏–≤–Ω–æ—Å—Ç—å –∫–Ω–æ–ø–æ–∫
     *
     * @param  {string|undefined} info.background –ö–∞—Ä—Ç–∏–Ω–∫–∞ —Å–ª–∞–π–¥–∞
     *
     * @param  {function[]} info.buttonaction –¢–µ–∫—Å—Ç —Å–ª–∞–π–¥–∞
     *
     * @param  {function} info.condition –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –¥–µ–π—Å—Ç–≤–∏—è –ø—Ä–∏ –≤–∫–ª—é—á–µ–Ω–∏–∏ —Å–ª–∞–π–¥–∞
     *
     */
    constructor(info) {
        this.text = info.text || '';
        this.buttontext = info.buttontext;
        this.buttonaction = info.buttonaction;
        this.buttonactive = info.buttonactive;
        this.background = info.background || "";
        this.condition = info.condition;
    }

    /** –ó–∞–ø—É—Å—Ç–∏—Ç—å —Å—Ü–µ–Ω—É */
    Begin() {
        LastSlide.add(this);
        setTimeout(() => {Game.LastSave.Save(this);},250);
        if (this.background == '') PictureField.style.display = 'none';
        else {
                    PictureField.src = ROOTPATH + 'pictures/' + this.background + '.png';
                    PictureField.style.display = 'block';
        }
        if (this.condition) this.condition();
        TextField.innerHTML = this.text.replace("$–ò–º—è –ò–≥—Ä–æ–∫–∞$", Game.PlayerName);
        this.InterfaceChecker();
    }

    /** –û—Ç–ø—Ä–∞–≤–Ω–∞—è —Ç–æ—á–∫–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ —ç–ª–µ–º–µ–Ω—Ç–æ–≤ —Å–ª–∞–π–¥–∞ */
    InterfaceChecker() {
        this.HideUnusableButtons();
        this.HideOnlyButton();
        this.SetButtonValues();
        this.HidePicture();
    }

    /** –ü—Ä—è—á–µ–º –Ω–µ–∏—Å–ø–æ–ª—å–∑—É–µ–º—ã–µ –∫–Ω–æ–ø–∫–∏ */
    HideUnusableButtons() {
        for (let x = 0; x < 5; x++) {
            document.getElementById(`b0${x}`).setAttribute('class', 'fade-in');
            setTimeout(() => { document.getElementById(`b0${x}`).setAttribute('class', 'show'); }, 1000);
            try {
                if (this.buttontext[x] == undefined || this.buttontext[x]=='') document.getElementById(`b0${x}`).style.display = 'none';
                else {
                    document.getElementById(`b0${x}`).style.display = 'block';
                    if (this.buttonactive[x] == false) document.getElementById(`b0${x}`).style.display = 'none';

                }
            }
            catch (error) { }
        }
    }

    /** –£—Å—Ç–∞–Ω–æ–≤–∏—Ç—å —Ç–µ–∫—Å—Ç –∫–Ω–æ–ø–æ–∫ –∏ –∑–∞–æ–¥–Ω–æ –∏–∑–º–µ–Ω–∏—Ç—å —Ä–∞–∑–º–µ—Ä */
    SetButtonValues() {
        for (let x = 0; x < this.buttontext.length; x++) {
            document.getElementById(`b0${x}`).innerHTML = this.buttontext[x];
            document.getElementById(`b0${x}`).onclick = this.buttonaction[x];

            if(document.getElementById(`b0${x}`).textContent.length <=28 ){
                document.getElementById(`b0${x}`).style.height = '6vh';
                document.getElementById(`b0${x}`).style.backgroundSize = '100% 6.1vh';
            }
            else{
                document.getElementById(`b0${x}`).style.height = '7.8vh';
                document.getElementById(`b0${x}`).style.backgroundSize = '105% 9.1vh';
            }
        }
    }

    /** –ü—Ä—è—á–µ–º –µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—É—é –∫–Ω–æ–ø–∫—É */
    HideOnlyButton() {
        if (this.buttontext.length == 1 && this.buttontext[0]=='' && this.background == '') {
            TextField.onclick = this.buttonaction[0];
            TextField.setAttribute('style', 'padding-top: 180px; height: 100%');
            document.getElementById(`b00`).style.display = 'none';
        }

        if (this.buttontext.length == 1 && this.buttontext[0]=='' && this.background != '') {
            TextField.onclick = this.buttonaction[0];
            TextField.setAttribute('style', 'padding-top: 0; height: 100%');
            document.getElementById(`b00`).style.display = 'none';
        }

        if (this.buttontext.length >= 2 && this.background == '') {
            TextField.onclick = () => { };
            TextField.setAttribute('style', 'padding-top: 150px; height: auto');
            document.getElementById(`b00`).style.vdisplay = 'block';
        }

        if (this.buttontext.length >= 2 && this.background != '') {
            TextField.onclick = () => { };
            TextField.setAttribute('style', 'padding-top: 0; height: auto');
            document.getElementById(`b00`).style.vdisplay = 'block';
        }
    }

    /** –û—Ç–∫–ª—é—á–∞–µ–º –∫–Ω–æ–ø–∫—É */
    Deactivate(a) {
        this.buttonactive[a] = false;
    }

    Activate(a) {
        this.buttonactive[a] = true;
    }

    /** –£–±–∏—Ä–∞–µ–º –∫–∞—Ä—Ç–∏–Ω–∫—É –µ—Å–ª–∏ –µ—ë –Ω–µ—Ç –∏ –º–µ–Ω—è–µ–º —Ä–∞—Å–ø–æ–ª–æ–∂–µ–Ω–∏–µ —Ç–µ–∫—Å—Ç–∞ */
    HidePicture() {
        if (this.background == '') {
            PictureField.style.display = 'none';
            BorderField.style.display = 'none';
        }
        else {
            PictureField.style.display = 'block';
            BorderField.style.display = 'block';
            BorderField.setAttribute('class', 'fade-in');
            TextField.setAttribute('class', 'fade-in');
            setTimeout(() => { TextField.setAttribute('class', 'show'); }, 1000);

        }
    }
}

/** –ö–ª–∞—Å—Å "–æ—Ç–Ω–æ—à–µ–Ω–∏–π" (–ø–µ—Ä–µ–º–µ–Ω–Ω—ã—Ö) –Ω–∞ –∫–æ—Ç–æ—Ä—ã–µ –º–æ–≥—É—Ç –ø–æ–≤–ª–∏—è—Ç –∏–≥—Ä–æ–∫–∏ */

class Stat {

    /**
     * @param {Object} info –û–±—ä–µ–∫—Ç —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏ "–æ—Ç–Ω–æ—à–µ–Ω–∏–π"
     * @param {string} info.name –ò–º—è –∏–ª–∏ –Ω–∞–∑–≤–∞–Ω–∏–µ
     * @param {number|undefined} info.attitude –ù–∞—á–∞–ª—å–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ
     * @param {string|undefined} info.title –ö—Ä–∞—Ç–∫–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ
     * @param {string|undefined} info.text –ü–æ–ª–Ω–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ
     * @param {string|undefined} info.type –¢–∏–ø
     * @param {string|undefined} info.picture –ö–∞—Ä—Ç–∏–Ω–∫–∞
     * @param {boolean|undefined} info.show –ü–æ–∫–∞–∑–∞—Ç—å –∏–∑–Ω–∞—á–∞–ª—å–Ω–æ –≤ –Ω–∏–≤–µ–Ω—Ç–∞—Ä–µ?
     * @param {string} info.story –ò—Å—Ç–æ—Ä–∏—è –∫ –∫–æ—Ç–æ—Ä–æ–π –ø—Ä–∏–≤—è–∑–∞–Ω —Å—Ç–∞—Ç
     */

    constructor(info) {
        this.name = info.name || '';
        this.attitude = info.attitude || 0;
        this.title = info.title || '';
        this.text = info.text || '';
        this.type = info.type || 'Person';
        this.picture = info.picture || '';
        this.show = info.show || false;
        this.story = info.story;
        this.CreateTable();
    }

    /** –î–æ–±–∞–≤–ª—è–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ. –ï—Å–ª–∏ –¥–æ–±–∞–≤–∏–ª–∏ –∑–Ω–∞—á–µ–Ω–∏–µ –∫ "–æ—Ç–Ω–æ—à–µ–Ω–∏—é" –∏ –µ—Å–ª–∏ —ç—Ç–æ –Ω–µ –≤—ã–±–æ—Ä, —Ç–æ –æ–Ω –ø–æ—è–≤–ª—è–µ—Ç—Å—è –≤ –∏–Ω–≤–µ–Ω—Ç–∞—Ä–µ, –µ—Å–ª–∏ –ø—Ä–µ–¥–º–µ—Ç, —Ç–æ –µ—Å–ª–∏ —Ä–∞–≤–µ–Ω –Ω—É–ª—é, —Ç–æ –∏—Å—á–µ–∑–∞–µ—Ç
     *  @param {number} v –ó–Ω–∞—á–µ–Ω–∏–µ
     */

    Add(v) {
        if(this.type != 'Choice') {
            OpenInventoryButton.setAttribute('class', 'blink');
        }

        this.show = true;
        this.attitude += v;
        if (this.type == 'Choice') {
            Game.SendData('–≤—ã–±–∏—Ä–∞–µ—Ç '+this.name+': '+this.attitude);
        }
        if (this.picture != '') {
            this.container.style.display = 'inline-block';
            if (this.type == 'Person') {
                this.SetEmoji();
            }
            if (this.type == 'Item') {
                this.SetAmount();
            }
           
        }

        if (this.type == 'Item' && this.attitude <= 0) this.container.style.display = 'none';

    }

    /** –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ—Ç –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ
     * @param {number} a –ó–Ω–∞—á–µ–Ω–∏–µ
     */

    Set(a) {
        this.attitude = a;
        this.Add(0);
    }

    SetName(a){
    this.name = a;
    this.SetEmoji();
    }

    /** –ü–æ–ª—É—á–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ */

    Get() {
        return (this.attitude);
    }

    /** –°–æ–∑–¥–∞—ë–º –∏ –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã –≤ –∏–Ω–≤–µ–Ω—Ç–∞—Ä—å */

    CreateTable() {
        if (this.picture != '') {
            this.container = document.createElement('cont');
            this.container.id = 'atttablecell';
            this.container.style.display = 'none';
            this.textinfo = document.createElement('te');
            this.cell = document.createElement('img');
            this.container.appendChild(this.cell);
            this.container.appendChild(this.textinfo);
            this.cell.src = ROOTPATH+'pictures/' + this.picture + '.png';
            if (this.type == 'Person') {
                this.cell.id = 'atttablecellpict';
                AttitudeTableField.appendChild(this.container);
            }
            if (this.type == 'Item') {
                this.cell.id = 'itemtablecellpict';
                Inventory.appendChild(this.container);
            }
            this.container.addEventListener('click', () => {
                setTimeout(() => {
                    InfoPicture.setAttribute('class', 'show');
                    InfoText.setAttribute('class','show');
                    InfoArticle.setAttribute('class','show');
                    }, 0);
                setTimeout(() => {
                    InfoPicture.src = ROOTPATH + 'pictures/' + this.picture + '.png';
                    InfoPicture.setAttribute('class', 'typewriter');
                    InfoText.setAttribute('class','typewriter');
                    InfoArticle.setAttribute('class','typewriter');
                    InfoText.innerHTML = this.title;
                    InfoArticle.innerHTML = '<hr>' + this.text;
                    }, 5);


            });

        }
    }

    /** –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —ç–º–æ–¥–∑–∏ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –∑–Ω–∞—á–µ–Ω–∏—è */

    SetEmoji() {
        if (this.attitude <= -1) this.textinfo.innerHTML = '<emoji>üôÅ</emoji><a>' + this.name;

        if (this.attitude == 0) this.textinfo.innerHTML = '<emoji>üò∂</emoji><a>' + this.name;

        if (this.attitude >= 1) this.textinfo.innerHTML = '<emoji>üòå</emoji><a>' + this.name;

        if (this.attitude >= 6) this.textinfo.innerHTML = '<emoji>üòè</emoji><a>' + this.name;

        if (this.attitude >= 10) this.textinfo.innerHTML = '<emoji>ü•∞</emoji><a>' + this.name;
        
    }

    /** –î–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ –ø—Ä–µ–¥–º–µ—Ç–∞ */

    SetAmount() {
        if(this.attitude>=2) {
            this.textinfo.innerHTML = '<amount>' + this.attitude + '</amount><a>' + this.name;
        }
        else{
            this.textinfo.innerHTML = '<amount>' + '</amount><a>' + this.name;
        }
    }

    /** –ü—Ä—è—á–µ–º —ç–ª–µ–º–µ–Ω—Ç */

    Hide() {
        try {
            this.attitude = 0;
            this.container.style.display = 'none';
            
        } catch (error) {

        }

    }
}
/** –ò—Å—Ç–æ—Ä–∏–∏ –≤ –º–µ–Ω—é */

class Story {

    /** @param {Object} values –ü–µ—Ä–µ–¥–∞—ë–º –∫–∞—Ä—Ç–∏–Ω–∫—É –∏ —Å—Ä–∞–∑—É –∂–µ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≥–ª–∞–≤—ã
     * @param {String} values.pict –û–±–ª–æ–∂–∫–∞ –∏—Å—Ç–æ—Ä–∏–∏
     * @param {Array} values.chapters –ì–ª–∞–≤—ã –∏—Å—Ç–æ—Ä–∏–∏
     */

    constructor(values) {
        this.pict = values.pict;
        this.chapters = values.chapters;
        this.Init();
    }

    /** –°–æ–∑–¥–∞—ë–º –∏ –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã —á–∞—Å—Ç–µ–π */

    Init(){
        this.story = document.createElement('part');
        this.img = document.createElement('img');
        this.img.src = 'pictures/' + this.pict + '.png';
        this.story.onclick = () => { ChapterField.innerHTML = ''; this.BuildChapters() };
        StoriesField.appendChild(this.story);
        this.story.appendChild(this.img);

    }

    /** –°–æ–∑–¥–∞—ë–º –∏ –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã –≥–ª–∞–≤ */

    BuildChapters(){
        this.backbutton = document.createElement('button');
        this.backbutton.onclick = () => { CloseOpen(ChapterField,StoriesField); ChapterField.innerHTML = '';this.backbutton.remove();  }
        ChapterField.appendChild(this.backbutton);
        for(let x=0;x<this.chapters.length;x++){
            this.chapters[x].Init();
            CloseOpen(StoriesField,ChapterField);
        }
    }
}

class Chapter {

    /** @param {Object} values –ü–µ—Ä–µ–¥–∞—ë–º –Ω–∞–∑–≤–∞–Ω–∏–µ –≥–ª–∞–≤—ã, –∫–∞—Ä—Ç–∏–Ω–∫—É –∏ —Å—Ä–∞–∑—É –∂–µ —á–∞—Å—Ç–∏
     * @param {String} values.name  –ù–∞–∑–≤–∞–Ω–∏–µ –≥–ª–∞–≤—ã
     * @param {String} values.pict –û–±–ª–æ–∂–∫–∞ –≥–ª–∞–≤—ã
     * @param {Array} values.parts –ß–∞—Å—Ç–∏ –≥–ª–∞–≤—ã
     */

    constructor(values) {
        this.name = values.name;
        this.pict = values.pict;
        this.parts = values.parts;
    }

    /** –°–æ–∑–¥–∞—ë–º –∏ –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã –≥–ª–∞–≤ */

    Init() {
        this.chapter = document.createElement('part');
        this.img = document.createElement('img');
        this.img.src = 'pictures/' + this.pict + '.png';
        this.button = document.createElement('button');
        this.button.innerText = this.name;
        this.chapter.onclick = () => { this.BuildParts();};
        ChapterField.appendChild(this.chapter);
        this.chapter.appendChild(this.img);
        this.chapter.appendChild(this.button);
    }

    /** –°–æ–∑–¥–∞—ë–º –∏ –¥–æ–±–∞–≤–ª—è–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã —á–∞—Å—Ç–µ–π */

    BuildParts() {
        this.backbutton = document.createElement('button');
        this.backbutton.onclick = () => { CloseOpen(PartField,ChapterField); PartField.innerHTML = ''; this.backbutton.remove();  }
        PartField.appendChild(this.backbutton);
        CloseOpen(ChapterField,PartField);
        for (var x = 0; x < this.parts.length; x++) {
            if (localStorage.getItem(this.parts[x].code+'_Played')=='1' || localStorage.getItem(this.parts[x].code+'_God')!=null || x===0) {
                this.parts[x].part = document.createElement('part');
                this.parts[x].img = document.createElement('img');
                this.parts[x].img.src = ROOTPATH + 'pictures/' + this.parts[x].pict + '.png';
                this.parts[x].button = document.createElement('button');
                this.parts[x].button.innerText = this.parts[x].name;
                this.parts[x].part.onclick = this.parts[x].event;
                PartField.appendChild(this.parts[x].part);
                this.parts[x].part.appendChild(this.parts[x].img);
                this.parts[x].part.appendChild(this.parts[x].button);
            }
        }
    }
}

