Game.AskName = function (action) {
    this.checkname = () => {
        this.name = this.input.value;
        if (this.name.length <= 1)this.text.innerText = '–ù–µ –º–µ–Ω–µ–µ 2 —Å–∏–º–≤–æ–ª–æ–≤!';
        else if (this.name.length >= 15)this.text.innerText = '–ú–∞–∫—Å–∏–º—É–º 15 —Å–∏–º–≤–æ–ª–æ–≤!';
        else if (!/^[–∞-—è—ë]*$/i.test(this.name))this.text.innerText = '–¢–æ–ª—å–∫–æ —Ä—É—Å—Å–∫–∏–µ –±—É–∫–≤—ã!';
        else {
            Game.PlayerName = this.name;
            action();
            MainField.style.display = 'block';
            this.im.remove();
            Game.SendData('—É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ—Ç –Ω–æ–≤–æ–µ –∏–º—è');
            localStorage.setItem('PlayerName',Game.PlayerName);
        }
    };
    MainField.style.display = 'none';
    this.action = action;
    this.im = document.createElement('im');
    this.text = document.createElement('p');
    this.text.innerText = '–ö–∞–∫ –º–µ–Ω—è –∑–æ–≤—É—Ç?'
    this.input = document.createElement('input');
    this.button = document.createElement('button');
    this.button.innerHTML = '–ü—Ä–∏–Ω—è—Ç—å';
    this.button.onclick = this.checkname;
    document.body.appendChild(this.im);
    this.im.appendChild(this.text);
    this.im.appendChild(this.input);
    this.im.appendChild(this.button);
}

