        picturesLoaded++;
        this.Interface.$('StartGameLoadingProgress').setAttribute('value', picturesLoaded);
        this.Interface.$('StartGameLoadingPercent').innerText = Math.floor(picturesLoaded/imagesPrechached.length*100) + '%';
