  a.href = URL.createObjectURL(file);
  a.innerText = '–°–∫–∞—á–∞—Ç—å –ø—Ä–æ–≥—Ä–µ—Å—Å'
  a.download = 'Chronicles_Progress';
  document.body.appendChild(a)
